	/**
	 * @param args
	 */
	public static void main(String[] args) {
		List<Annotation> annotations = loadData();
		
		Map<String, String> mapping = getMapping(mappingFile);

		for (Annotation annotation : annotations) {
			String meketreUrl = annotation.getMeketreUrl();
			if (meketreUrl != null) {
				annotation.setPelagiosUrl(mapping.get(meketreUrl + ".html"));
			}
		}
		
		writeRDF(resultFile, annotations);
	}

	private static List<Annotation> loadData() {
		List<Annotation> annotations = new ArrayList<Annotation>();
		
		// load texts and urls
		TermMap terms = TermMap.getInstance();
		for (Tupel tupel : Tupel.parseTupels(termScript)) {
			terms.putTerm(tupel);
		}

		// load fragments
		for (Tupel tupel : Tupel.parseTupels(fragScript)) {
			annotations.add(new Fragment(tupel));
		}

		// load tombs
		for (Tupel tupel : Tupel.parseTupels(tombScript)) {
			annotations.add(new Tomb(tupel));
		}

		// load themes
		List<Theme> themes = new ArrayList<Theme>();
		for (Tupel tupel : Tupel.parseTupels(themeScript)) {
			Theme theme = new Theme(tupel, annotations);
			themes.add(theme);
		}
		annotations.addAll(themes);
		
		return annotations;
	}

	/**
	 * Loads the Meketre- to Pelagios-mapping and fills the pelagiosUrl of all
	 * Annotation.
	 */
	private static Map<String, String> getMapping(String filename) {
		BufferedReader in = null;

		try {
			in = new BufferedReader(new FileReader(filename));

			// load the mapping of Meketre- and Pelagio-places
			Map<String, String> mapping = new TreeMap<String, String>();
			String meketreUrl;
			while ((meketreUrl = in.readLine()) != null) {
				String pelagiosUrl = in.readLine();

				if (!"???".equals(pelagiosUrl)) {
					mapping.put(meketreUrl, pelagiosUrl);
				}

				in.readLine();
			}
			
			return mapping;

		} catch (IOException ex) {
			ex.printStackTrace();
			
			return null;
		} finally {
			if (in != null) {
				try {
					in.close();
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
		}
	}

	private static void writeRDF(String filename, List<Annotation> annotations) {
		PrintStream out = null;

		try {
			File f = new File(filename);
			f.delete();
			out = new PrintStream(filename);

			out.println("<?xml version=\"1.0\" encoding=\"UTF-8\"?>");
			out.println("<rdf:RDF xmlns:rdf=\"http://www.w3.org/1999/02/22-rdf-syntax-ns#\"");
			out.println("    xmlns:dct=\"http://purl.org/dc/terms/\"");
			out.println("    xmlns:tei=\"http://www.tei-c.org/ns/1.0\"");
			out.println("    xmlns:saws=\"http://www.purl.org/saws/ontology#\"");
			out.println("    xmlns:oac=\"http://www.openannotation.org/ns/\">);");
			out.println();

			for (Annotation annotation : annotations) {
				annotation.write(out);
			}

			out.println("</rdf:RDF>");

		} catch (IOException ex) {
			ex.printStackTrace();
		} finally {
			if (out != null) {
				out.close();
			}
		}
	}
